CREATE TABLE client_updates (
	client_id			integer,
	pcc					varchar(240),
	client_name			varchar(240),
	address				varchar(240),
	zip_code			varchar(240),
	premises			varchar(240),
	street				varchar(240),
	division			varchar(240),
	town				varchar(240),
	telephone_numbers	varchar(240),
	email				varchar(240),
	website				varchar(240),
	technical_contact	varchar(240),
	travel_manager		varchar(240),
	note				varchar(240)
);




UPDATE clients SET address = client_updates.address, zipcode = client_updates.zip_code, 
	premises = client_updates.premises, street = client_updates.street, division = client_updates.division,
	town = client_updates.town, telno = client_updates.telephone_numbers, email = client_updates.email,
	website = client_updates.website, travel_manager = client_updates.travel_manager, 
	technical_contact = client_updates.technical_contact, details = client_updates.note, address_updated = true
FROM client_updates WHERE clients.client_id = client_updates.client_id;

UPDATE clients SET details = null WHERE length(trim(details)) = 0;

UPDATE clients SET is_active = false WHERE details is not null;

